import axios from "axios";

const URL = `https://jsonplaceholder.typicode.com/posts`;

export default class Api {
  static fetchData = async () => await axios.get(`${URL}`);
  static fetchSingleData = async id => await axios.get(`${URL}/${id}`);
}
