import React from "react";
import Routing from "./Routing";
import "./App.scss";
import { IntlProvider } from "react-intl";
import translation from './translation';
import {connect} from 'react-redux';
import {setCurrentLang} from '../Redux/Actions/index';
import { injectIntl } from 'react-intl';



export class App extends React.Component {
  setLanguage = lang => {

    this.props.setCurrentLang(lang);
  };

  render() {
    const {lang}=this.props;
    return (
      <IntlProvider locale={lang} messages={translation[lang]}>
        <div  className={lang === 'ar' ? 'rtl' : 'ltr'} dir={lang === 'ar' ? 'rtl' : 'ltr'}>
       <p>ahahas</p>
       <button  className="btn btn-dark mr-3 btn-sm" id="en" onClick={() => this.setLanguage('en')}>English</button>
                <button  className="btn btn-primary mr-3 btn-sm" id="ar" onClick={() => this.setLanguage('ar')}>العربية</button>

          <Routing />
          </div>

      </IntlProvider>
    );
  }

}
const mapStateToProps = state => {
  console.log("state",state);

  // return {
  //   lang: state.locale.lang
  // }
}

export default connect(null,{setCurrentLang})(App);
