import axios from "axios";

const URL = `https://jsonplaceholder.typicode.com/posts`;
const axiosInstance = axios.create({
  baseURL: 'https://jsonplaceholder.typicode.com/posts'
})

const isHandlerEnabled = (config={}) => {
  return config.hasOwnProperty('handlerEnabled') && !config.handlerEnabled ? 
    false : true
}

const requestHandler = (request) => {
  if (isHandlerEnabled(request)) {
    // Modify request here
    request.headers['X-CodePen'] = 'https://codepen.io/teroauralinna/full/vPvKWe'
  }
  return request
}

axiosInstance.interceptors.request.use(
  request => requestHandler(request)
)

export default class Api {
  static fetchData = async () => await axios.get(axiosInstance.baseURL);
  static fetchSingleData = async id => await axios.get(`${URL}/${id}`);
  static postData = async () => await axios.post(`${URL}`);
}
